def create_config_table(qgofer_cache_db) -> None:
    """Create the qgofer config table."""
    qgofer_db_wrapper = QGoferDBWrapper(qgofer_cache_db)
    qgofer_db_wrapper.create_table("qgofer_config", "key TEXT UNIQUE,value TEXT")

