def store_hugging_face_token(API_TOKEN, qgofer_cache_db) -> None:
    """store the hugging face api token"""
    qgofer_db_wrapper = QGoferDBWrapper(qgofer_cache_db)
    qgofer_db_wrapper.insert(
        "qgofer_config",
        columns="key, value",
        values=f"'HUGGING_FACE_KEY', '{API_TOKEN}'",
    )
